// DURATION Computation from Starting Date
function toNum(v, def) {
    v = Number(v);
    return isNaN(v) ? def : v;
}

var durMs = toNum(value, 0);
var label = "";

if (durMs <= 0) {
    return "";
}

var totalSeconds = Math.floor(durMs / 1000);
var totalMinutes = Math.floor(totalSeconds / 60);
var totalHours   = Math.floor(totalMinutes / 60);
var totalDays    = Math.floor(totalHours / 24);

var seconds = totalSeconds % 60;
var minutes = totalMinutes % 60;
var hours   = totalHours % 24;

if (totalMinutes < 1) {
    label = totalSeconds + " s";
} else if (totalMinutes < 60) {
    label = totalMinutes + " min";
} else if (totalHours < 24) {
    label = hours + "h " + minutes + "m";
} else {
    label = totalDays + "d " + hours + "h " + minutes + "m";
}

return label;

// DATE - convert long integer to duration Normalise to milliseconds: handle both seconds and ms
if (value == null || value === "" || value === 0) {
    return "---";
}

var ts = value * 1; // numeric
if (ts < 1e11) {
    // too small to be ms  treat as seconds
    ts = ts * 1000;
}

var now = Date.now();
var diff = now - ts;
if (diff < 0) diff = 0; // safety

var totalMinutes = Math.floor(diff / 60000);

// < 60 min   "XX min"
if (totalMinutes < 60) {
    return totalMinutes + " min";
}

// 124 h  "Hh Mm"
var hours = Math.floor(totalMinutes / 60);
var minutes = totalMinutes % 60;

if (hours < 24) {
    return hours + "h " + minutes + "m";
}

//  24 h  "Dd Hh Mm"
var days = Math.floor(hours / 24);
var remHours = hours % 24;

return days + "d " + remHours + "h " + minutes + "m";

