let v = value;

if (typeof v === "string") {
  try { v = JSON.parse(v); } catch (e) { return value; }
}
if (!v || typeof v !== "object") return value;

function fmtMs(ms) {
  ms = Number(ms);
  if (!isFinite(ms) || ms < 0) return "";
  if (ms < 1000) return ms.toFixed(0) + " ms";
  return (ms / 1000).toFixed(2) + " s";
}

function fmtSeconds(s) {
  s = Number(s);
  if (!isFinite(s) || s < 0) return "";
  const days = Math.floor(s / 86400); s %= 86400;
  const hrs  = Math.floor(s / 3600);  s %= 3600;
  const mins = Math.floor(s / 60);
  const secs = Math.floor(s % 60);
  const parts = [];
  if (days) parts.push(days + "d");
  if (hrs) parts.push(hrs + "h");
  if (mins) parts.push(mins + "m");
  parts.push(secs + "s");
  return parts.join(" ");
}

const bootCounter = (v.bootCounter ?? "");
const bootTimeMs = (v.bootTime !== undefined) ? fmtMs(v.bootTime) : "";
const sinceBoot = (v.timeSinceLastBoot !== undefined) ? fmtSeconds(v.timeSinceLastBoot) : "";

return "Boot #" + bootCounter +
       " | Boot time: " + bootTimeMs +
       " | Uptime: " + sinceBoot;

