let v = value;

// Parse JSON string
if (typeof v === "string") {
  try { v = JSON.parse(v); } catch (e) { return ""; } // if unreadable, hide
}

if (!v || typeof v !== "object") return "";

// If no keys => no faults, hide the row
const ids = Object.keys(v);
if (!ids.length) return "";

// Proper labels
const map = {
  0: "RGB all broken",
  1: "Red + Green broken",
  2: "Red + Blue broken",
  3: "Red broken",
  4: "Green + Blue broken",
  5: "Green broken",
  6: "Blue broken",
  7: "OK"
};

const parts = ids
  .sort((a,b) => Number(a) - Number(b))
  .map(id => {
    const code = Number(v[id]);
    // Dont show OK entries if they ever appear
    if (code === 7) return null;
    const label = (map[code] !== undefined) ? map[code] : ("Unknown(" + v[id] + ")");
    return id + ": " + label;
  })
  .filter(Boolean);

// If after filtering nothing remains, hide row
if (!parts.length) return "";

return parts.join(" | ");

rowClick.customFunction:
var $injector = widgetContext.$scope.$injector;
var dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

function esc(s) {
  return String(s)
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#39;');
}

var ap = additionalParams || {};
var row = ap.row || ap.rowData || ap.data || ap;

var ts = row.formattedTs || row.ts || row.timestamp || row.time || '';
var raw = row['1'] || row.value || row.faults || '';  // your long "4: RGB..." string

var lines = [];
if (typeof raw === 'string' && raw.length) {
  lines = raw.split('|').map(s => s.trim()).filter(Boolean);
}

// Build HTML with <br>
var html = '';
if (ts) html += '<div><strong>Time:</strong> ' + esc(ts) + '</div><br>';

if (lines.length) {
  html += '<div><strong>Faults:</strong></div>';
  html += '<div style="line-height:1.5; margin-top:6px;">' +
          lines.map(l => '&bull; ' + esc(l)).join('<br>') +
          '</div>';
} else {
  html += '<div>No fault details available.</div><br>';
  html += '<pre style="white-space:pre-wrap; margin:0;">' + esc(JSON.stringify(row, null, 2)) + '</pre>';
}

dialogs.confirm('Stud Fault Details', html, 'Close', 'OK').subscribe();

