configuration.jsScript:
// ─── CONSTANTS ────────────────────────────────────────────
var STALE_MS = 90000;
var OFFLINE_MS = 150000;
var DISPATCH_THRESHOLD_MS = 900000; // 15 min

var ERROR_DESCRIPTIONS = {
    0: "No error",
    1: "Track turned off",
    2: "Header corrupt",
    3: "Body corrupt",
    4: "Forward comms length mismatch",
    5: "Unrecognised function",
    6: "Return comms disabled",
    7: "Not valid return comms function",
    8: "Cannot send to group",
    9: "No return comms data received",
    10: "Return comms parity bit error",
    11: "Return comms checksum error",
    12: "Track setting locked by input",
    13: "Flashing interval array not programmed",
    14: "Flashing timing array not programmed"
};

// ─── HELPERS ──────────────────────────────────────────────
// Will disable for now can be used later
/*function formatDuration(ms) {
    if (ms <= 0) return "0 min";
    var totalSeconds = Math.floor(ms / 1000);
    var days = Math.floor(totalSeconds / 86400);
    var hours = Math.floor((totalSeconds % 86400) / 3600);
    var minutes = Math.floor((totalSeconds % 3600) / 60);
    if (days > 0) return days + "d " + hours + "h " +
        minutes + "min";
    if (hours > 0) return hours + "h " + minutes + "min";
    return minutes + "min";
}
*/

function countSetBits(n) {
    var count = 0;
    var num = Number(n);
    while (num > 0) {
        if (num % 2 === 1) count++;
        num = Math.floor(num / 2);
    }
    return count;
}

function errorDescription(code) {
    return ERROR_DESCRIPTIONS[code] || (
        "Unknown error code " + code);
}

// ─── MAIN LOGIC ───────────────────────────────────────────
var now = Date.now();
var last = Number(metadata.ss_latestHbTs || 0);

if (!last) {
    return {
        msg: {},
        metadata: metadata,
        msgType: "POST_ATTRIBUTES_REQUEST"
    };
}

var age = now - last;

// STATE DETECTION
var state = "ONLINE";
if (age >= OFFLINE_MS) state = "OFFLINE";
else if (age >= STALE_MS) state = "STALE";

var prev = String(metadata.ss_psuState || "");


// FAULT & TRACK DATA
var hasPsuFault = String(metadata.cs_psuFault) === "true";
var errorCode = Number(metadata.cs_psuLastErrorCode || 0);
var hasError = errorCode !== 0;
var studFaultRaw = Number(metadata.studFaults || 0);
var faultedStuds = countSetBits(studFaultRaw);
var hasStudFault = faultedStuds > 0;
var numStuds = Number(metadata.cs_numStuds || 0);
var goodStuds = Math.max(0,numStuds - faultedStuds);
var trackOn = String(metadata.trackOn) === "true";
var hasAnyFault = hasPsuFault || hasError;

// OFFLINE DURATION 
var offlineSince = Number(metadata.ss_offlineSinceTs || 0);
if (state === "OFFLINE" && offlineSince === 0) {
    offlineSince = last;
}
var offlineDuration = offlineSince > 0 ? now -
    offlineSince : 0;

// ATTRIBUTES & TELEMETRY OUTPUT
var out = {
    psuState: state
    
};
var telem = {
    eventType: state + "_START"
};

telem.okStuds = goodStuds;

if (state === "STALE") telem.staleEvent = 1;
if (state === "OFFLINE") telem.offlineEvent = 1;

if (state === "OFFLINE") {
    out.offlineSinceTs = last;
}

if (prev === "OFFLINE" && state === "ONLINE") {
    var since = Number(metadata.ss_offlineSinceTs || 0);
    if (since > 0) telem.offlineOutageDurationMs = now -
        since;
    out.offlineSinceTs = 0;
}

// ─── DISPATCH RECOMMENDATION + REASON ────────────────────
var dispatch;
var reason;

if (state === "ONLINE") {

    if (hasAnyFault && !trackOn) {
        // Worst online case — fault + track down
        dispatch = "DISPATCH";
        if (hasPsuFault && hasError) {
            reason =
                "Hardware fault signal active (fault flag + error code " +
                errorCode + ") and track is shut down. Technician required immediately.";
        } else if (hasPsuFault) {
            reason =
                "Hardware fault flag active and track is shut down. Inspect PSU hardware immediately.";
        } else {
            reason = "Has error code " + errorCode + 
                " active and track is shut down. Hardware fault likely caused shutdown. Technician required.";
        }

    } else if (hasAnyFault && trackOn) {
        // Fault present but track still running
        dispatch = "INVESTIGATE";
        if (hasPsuFault && hasError) {
            reason =
                "Hardware fault signal detected (fault flag + error code " +
                errorCode + ". Track still running but fault may escalate. Inspect PSU.";
        } else if (hasPsuFault) {
            reason =
                "Hardware fault flag active. Track still running but may shut down. Inspect PSU before issue escalates.";
        } else {
            reason = "Error code " + errorCode + " detected. Track still running. Investigate before condition worsens.";
        }

    } else if (!trackOn && !hasAnyFault) {
        // Track off with no PSU fault — unexpected
        dispatch = "INVESTIGATE";
        reason =
            "Track is shut down with no PSU fault signal detected. Unexpected shutdown — verify control inputs and physical track state.";

    } else if (hasStudFault && trackOn) {
        // Stud faults only, PSU healthy, track running
        dispatch = "MONITOR";
        if (numStuds > 0) {
            reason = faultedStuds + " of " + numStuds +
                " studs reporting faults. PSU operating normally and track running. Schedule stud inspection.";
        } else {
            reason =
                "Stud faults detected. PSU operating normally and track running. Schedule stud inspection.";
        }

    } else if (hasStudFault && !trackOn) {
        // Stud faults + track off, no PSU fault
        dispatch = "INVESTIGATE";
        if (numStuds > 0) {
            reason = "Track is shut down and " +
                faultedStuds + " of " + numStuds +
                " studs reporting faults. No PSU fault detected — investigate track state and stud health.";
        } else {
            reason =
                "Track is shut down and stud faults detected. No PSU fault — investigate track state and stud health.";
        }

    } else {
        // All clear
        dispatch = "NO_ACTION";
        reason =
            "PSU online, track running, no faults detected. System operating normally.";
    }

} else if (state === "STALE") {

    if (hasAnyFault || (!trackOn)) {
        // Any fault or track off during comms degradation
        dispatch = "INVESTIGATE";
        if (!trackOn && hasAnyFault) {
            reason =
                "Communication degrading. Last known state: track shut down and fault signal active. " +
                "Comms loss may be masking serious hardware issue. Prioritize investigation.";
        } else if (!trackOn) {
            reason =
                "Communication degrading. Last known state: track was shut down. " +
                "Signal degradation with track shutdown requires investigation.";
        } else {
            reason =
                "Communication degrading and fault signal active at last heartbeat. " +
                "Signal loss may be masking real hardware issue. Prioritize investigation.";
        }

    } else if (hasStudFault) {
        dispatch = "INVESTIGATE";
        if (numStuds > 0) {
            reason = "Communication degrading. " +
                faultedStuds + " of " + numStuds +
                " studs reported faults at last heartbeat. Monitor comms and stud health.";
        } else {
            reason =
                "Communication degrading. Stud faults reported at last heartbeat. Monitor comms and stud health.";
        }

    } else {
        dispatch = "MONITOR";
        reason =
            "Communication gap detected. No hardware faults at last heartbeat. Device typically self-recovers. Wait before acting.";
    }

} else if (state === "OFFLINE") {

    if (hasAnyFault) {
        // PSU-level fault while offline
        dispatch = "DISPATCH";
        if (hasPsuFault && hasError) {
            reason =
                "Communication lost. Last known state: hardware fault confirmed (fault flag + error code " +
                errorCode + "). Technician required.";
        } else if (hasPsuFault) {
            reason =
                "Communication lost. Last known state: hardware fault flag active. Technician required.";
        } else {
            reason =
                "Communication lost. Last known state: error code " +
                errorCode + " active. Technician required.";
        }

    } else if (hasStudFault && !trackOn) {
        // Stud faults + track off while offline
        dispatch = "INVESTIGATE";
        if (numStuds > 0) {
            reason =
                "Communication lost. Last known state: track shut down and " +
                faultedStuds + " of " + numStuds +
                " studs faulted. No PSU fault confirmed — check connectivity and stud health.";
        } else {
            reason =
                "Communication lost. Last known state: track shut down and stud faults active. No PSU fault confirmed — check connectivity and stud health.";
        }

    } else if (hasStudFault) {
        // Stud faults only while offline
        dispatch = "INVESTIGATE";
        if (numStuds > 0) {
            reason =
                "Communication lost. Last known state: " +
                faultedStuds + " of " + numStuds +
                " studs faulted. No PSU fault confirmed — investigate stud health and connectivity.";
        } else {
            reason =
                "Communication lost. Last known state: stud faults detected. No PSU fault confirmed — investigate stud health and connectivity.";
        }

    } else if (!trackOn) {
        // Track off + offline, no faults
        dispatch = "INVESTIGATE";
        reason =
            "Communication lost. Last known state: track was shut down with no fault signal. " +
            "Verify control inputs and connectivity before dispatching.";

    } else if (offlineDuration > DISPATCH_THRESHOLD_MS) {
        // Long outage, no faults, track was on
        dispatch = "INVESTIGATE";
        reason =
            "Extended communication loss with no hardware fault at last heartbeat. " +
            "Check connectivity and signal quality before dispatching.";

    } else {
        // Short outage, no faults, track was on
        dispatch = "MONITOR";
        reason =
            "Communication lost with no hardware fault detected at last heartbeat. " +
            "Likely signal issue. Continue monitoring.";
    }
}

out.dispatchRecommendation = dispatch;
out.dispatchReason = reason;

// EARLY EXIT: Only skip update if BOTH state AND recommendation unchanged
var prevRecommendation = String(metadata
    .ss_dispatchRecommendation || "");
if (prev === state && prevRecommendation === dispatch) {
    return {
        msg: {},
        metadata: metadata,
        msgType: "POST_ATTRIBUTES_REQUEST"
    };
}

return [{
        msg: out,
        metadata: metadata,
        msgType: "POST_ATTRIBUTES_REQUEST"
    },
    {
        msg: telem,
        metadata: metadata,
        msgType: "POST_TELEMETRY_REQUEST"
    }
];
