configuration.jsScript:
// ─── CONSTANTS ────────────────────────────────────────────
var STALE_MS = 90000;
var OFFLINE_MS = 150000;
var DISPATCH_THRESHOLD_MS = 900000; // 15 min

var ERROR_DESCRIPTIONS = {
    0: "No error",
    1: "Track turned off",
    2: "Header corrupt",
    3: "Body corrupt",
    4: "Forward comms length mismatch",
    5: "Unrecognised function",
    6: "Return comms disabled",
    7: "Not valid return comms function",
    8: "Cannot send to group",
    9: "No return comms data received",
    10: "Return comms parity bit error",
    11: "Return comms checksum error",
    12: "Track setting locked by input",
    13: "Flashing interval array not programmed",
    14: "Flashing timing array not programmed"
};

// ─── HELPERS ──────────────────────────────────────────────
function formatDuration(ms) {
    if (ms <= 0) return "0 min";
    var totalSeconds = Math.floor(ms / 1000);
    var days = Math.floor(totalSeconds / 86400);
    var hours = Math.floor((totalSeconds % 86400) / 3600);
    var minutes = Math.floor((totalSeconds % 3600) / 60);
    if (days > 0) return days + "d " + hours + "h " +
        minutes + "min";
    if (hours > 0) return hours + "h " + minutes + "min";
    return minutes + "min";
}

function countSetBits(n) {
    var count = 0;
    var num = Number(n);
    while (num > 0) {
        if (num % 2 === 1) count++;
        num = Math.floor(num / 2);
    }
    return count;
}

function errorDescription(code) {
    return ERROR_DESCRIPTIONS[code] || (
        "Unknown error code " + code);
}

// ─── MAIN LOGIC ───────────────────────────────────────────
var now = Date.now();
var last = Number(metadata.ss_latestHbTs || 0);

if (!last) {
    return {
        msg: {},
        metadata: metadata,
        msgType: "POST_ATTRIBUTES_REQUEST"
    };
}

var age = now - last;

// STATE DETECTION
var state = "ONLINE";
if (age >= OFFLINE_MS) state = "OFFLINE";
else if (age >= STALE_MS) state = "STALE";

var prev = String(metadata.ss_psuState || "");
if (prev === state) {
    return {
        msg: {},
        metadata: metadata,
        msgType: "POST_ATTRIBUTES_REQUEST"
    };
}

// FAULT & TRACK DATA
var hasPsuFault = String(metadata.cs_psuFault) === "true";
var errorCode = Number(metadata.cs_psuLastErrorCode || 0);
var hasError = errorCode !== 0;
var studFaultRaw = Number(metadata.studFaults || 0);
var faultedStuds = countSetBits(studFaultRaw);
var hasStudFault = faultedStuds > 0;
var numStuds = Number(metadata.cs_numStuds || 0);
var trackOn = String(metadata.trackOn) === "true";
var hasAnyFault = hasPsuFault || hasError;

// OFFLINE DURATION FIX
// offlineSinceTs hasn't been saved yet on first OFFLINE transition
// so we fall back to last heartbeat timestamp as the start
var offlineSince = Number(metadata.ss_offlineSinceTs || 0);
if (state === "OFFLINE" && offlineSince === 0) {
    offlineSince = last;
}
var offlineDuration = offlineSince > 0 ? now -
    offlineSince : 0;
var durationStr = formatDuration(offlineDuration);
var ageStr = formatDuration(age);

// ATTRIBUTES & TELEMETRY OUTPUT
var out = {
    psuState: state
};
var telem = {
    eventType: state + "_START"
};

if (state === "STALE") telem.staleEvent = 1;
if (state === "OFFLINE") telem.offlineEvent = 1;

if (state === "OFFLINE") {
    out.offlineSinceTs = last;
}

if (prev === "OFFLINE" && state === "ONLINE") {
    var since = Number(metadata.ss_offlineSinceTs || 0);
    if (since > 0) telem.offlineOutageDurationMs = now -
        since;
    out.offlineSinceTs = 0;
}

// ─── DISPATCH RECOMMENDATION + REASON ────────────────────
var dispatch;
var reason;

if (state === "ONLINE") {

    if (hasAnyFault && !trackOn) {
        // Worst online case — fault confirmed AND track is off
        dispatch = "DISPATCH";
        if (hasPsuFault && hasError) {
            reason =
                "Track is OFF. PSU fault flag active and error code " +
                errorCode +
                " (" + errorDescription(errorCode) +
                ") reported. Hardware fault likely caused track shutdown. Technician required.";
        } else if (hasPsuFault) {
            reason =
                "Track is OFF and PSU fault flag is active. Hardware fault likely caused track shutdown. Technician required.";
        } else {
            reason = "Track is OFF and error code " +
                errorCode +
                " (" + errorDescription(errorCode) +
                ") is active. Hardware fault likely caused track shutdown. Technician required.";
        }

    } else if (hasAnyFault && trackOn) {
        // Fault present but track still running — investigate before it escalates
        dispatch = "INVESTIGATE";
        if (hasPsuFault && hasError) {
            reason =
                "PSU fault flag active and error code " +
                errorCode +
                " (" + errorDescription(errorCode) +
                ") reported. Track is still ON but fault may escalate. Inspect PSU.";
        } else if (hasPsuFault) {
            reason =
                "PSU fault flag is active. Track is still ON but fault may cause shutdown. Monitor closely and inspect PSU.";
        } else {
            reason = "Error code " + errorCode + " (" +
                errorDescription(errorCode) +
                ") active. Track is still ON. Investigate before issue escalates.";
        }

    } else if (!trackOn && !hasAnyFault) {
        // Track off with no PSU fault — unexpected, needs investigation
        dispatch = "INVESTIGATE";
        reason =
            "Track is OFF but no PSU fault or error code detected. " +
            "Unexpected track shutdown — check control inputs and physical track state.";

    } else if (hasStudFault && trackOn) {
        // Stud faults only, PSU healthy, track running
        dispatch = "MONITOR";
        reason = faultedStuds + " of " + numStuds +
            " studs reporting a fault. " +
            "PSU is operating normally and track is ON. Schedule stud inspection.";

    } else if (hasStudFault && !trackOn) {
        // Stud faults + track off, no PSU fault — escalate
        dispatch = "INVESTIGATE";
        reason = "Track is OFF and " + faultedStuds +
            " of " + numStuds +
            " studs are reporting faults. No PSU fault detected. Investigate track and stud health.";

    } else {
        // All clear
        dispatch = "NO_ACTION";
        reason =
            "PSU is online, track is ON, and no faults detected. Operating normally.";
    }

} else if (state === "STALE") {

    if (hasAnyFault || (!trackOn)) {
        // Any fault or track off during comms degradation — escalate
        dispatch = "INVESTIGATE";
        if (!trackOn && hasAnyFault) {
            reason = "Comms gap detected for " + ageStr +
                ". Track is OFF and fault is active. " +
                "Comms loss may be masking a serious hardware issue. Prioritise investigation.";
        } else if (!trackOn) {
            reason = "Comms gap detected for " + ageStr +
                ". Track was OFF at last heartbeat. " +
                "Signal degradation combined with track shutdown — investigate.";
        } else {
            reason = "Comms gap detected for " + ageStr +
                " and a fault is active. " +
                "Signal loss may be masking a real hardware issue. Prioritise investigation.";
        }

    } else if (hasStudFault) {
        dispatch = "INVESTIGATE";
        reason = "Comms gap detected for " + ageStr + ". " +
            faultedStuds + " of " + numStuds +
            " studs were reporting faults at last heartbeat. Monitor comms and stud health.";

    } else {
        dispatch = "MONITOR";
        reason = "Comms gap detected for " + ageStr +
            ". No hardware fault detected. " +
            "Device typically self-recovers. Wait before acting.";
    }

} else if (state === "OFFLINE") {

    if (hasAnyFault) {
        // PSU-level fault while offline — definitive dispatch
        dispatch = "DISPATCH";
        if (hasPsuFault && hasError) {
            reason =
                "Hardware fault confirmed. Fault flag active and error code " +
                errorCode +
                " (" + errorDescription(errorCode) +
                "). Offline for " + durationStr +
                ". Technician required.";
        } else if (hasPsuFault) {
            reason =
                "Hardware fault confirmed. Fault flag active. Offline for " +
                durationStr + ". Technician required.";
        } else {
            reason =
                "Hardware fault confirmed. Error code " +
                errorCode +
                " (" + errorDescription(errorCode) +
                "). Offline for " + durationStr +
                ". Technician required.";
        }

    } else if (hasStudFault && !trackOn) {
        // Stud faults + track off while offline — escalate above monitor
        dispatch = "INVESTIGATE";
        reason = "Offline for " + durationStr +
            ". Track was OFF and " + faultedStuds + " of " +
            numStuds +
            " studs were reporting faults at last heartbeat. No PSU fault confirmed — check connectivity and stud health.";

    } else if (hasStudFault) {
        // Stud faults only while offline
        dispatch = "INVESTIGATE";
        reason = "Offline for " + durationStr + ". " +
            faultedStuds + " of " + numStuds +
            " studs were reporting faults at last heartbeat. No PSU fault confirmed — investigate stud health and connectivity.";

    } else if (!trackOn) {
        // Track off + offline, no faults
        dispatch = "INVESTIGATE";
        reason = "Offline for " + durationStr +
            ". Track was OFF at last heartbeat with no confirmed fault. " +
            "Check control inputs and connectivity before dispatching.";

    } else if (offlineDuration > DISPATCH_THRESHOLD_MS) {
        // Long outage, no faults, track was on
        dispatch = "INVESTIGATE";
        reason = "Offline for " + durationStr +
            " with no confirmed hardware fault. " +
            "Extended outage — connection or signal check recommended before dispatching.";

    } else {
        // Short outage, no faults, track was on — likely signal
        dispatch = "MONITOR";
        reason = "Offline for " + durationStr +
            " but no hardware fault detected. " +
            "Likely a signal issue. Continue monitoring.";
    }
}

out.dispatchRecommendation = dispatch;
out.dispatchReason = reason;

return [{
        msg: out,
        metadata: metadata,
        msgType: "POST_ATTRIBUTES_REQUEST"
    },
    {
        msg: telem,
        metadata: metadata,
        msgType: "POST_TELEMETRY_REQUEST"
    }
];
