// If heartbeat attribute isn't present, do nothing
if (msg == null || msg.heartbeat == null) {
    return { msg: msg, metadata: metadata, msgType: msgType };
}

// Heartbeat is a long integer in your case
var hb = Number(msg.heartbeat);
if (!isFinite(hb) || hb <= 0) {
    return { msg: msg, metadata: metadata, msgType: msgType };
}

// Decide what to store as latestHeartbeatTs:
// If heartbeat looks like an epoch timestamp in ms (13 digits), use it.
// Otherwise, use server receipt time.
if (hb > 1000000000000) {        // ~2001+ in ms epoch
    msg.latestHeartbeatTs = hb;
} else {
    msg.latestHeartbeatTs = Date.now();
}

// Add a debug marker so you can *prove* the node executed
msg._hb_seen = hb;

return { msg: msg, metadata: metadata, msgType: msgType };

