var nowTs = Date.now();

function toNum(v, def) {
    var n = Number(v);
    return isFinite(n) ? n : def;
}

var hbTs = toNum(metadata.ss_latestHeartbeatTs, 0);
var prevOfflineSinceTs = toNum(metadata.ss_offlineSinceTs, 0);

// Defaults (can become server attrs later)
var HB_INTERVAL_MS = 30000;
var STALE_AT = 3;   // missed >= 3
var OFFLINE_AT = 5; // missed >= 5

// Missed cycles
var missed = 0;
if (hbTs > 0) {
    var deltaMs = nowTs - hbTs;
    if (deltaMs < 0) deltaMs = 0;
    missed = Math.floor(deltaMs / HB_INTERVAL_MS);
}

// Status
var psuCommPath = "UNKNOWN";


if (hbTs > 0) {
    if (missed >= OFFLINE_AT) {
        psuCommPath = "OFFLINE";
    } else if (missed >= STALE_AT) {
        psuCommPath = "STALE";
    } else {
        psuCommPath = "OK";
    }
}

// offlineSinceTs: set once on OFFLINE entry, reset on recovery
var previouslyOffline = (prevOfflineSinceTs > 0);
var currentlyOffline = (psuCommPath === "OFFLINE");

var offlineSinceTs = 0;

if (currentlyOffline) {
    if (previouslyOffline) {
        offlineSinceTs = prevOfflineSinceTs;
    } else {
        // official offline start = last heartbeat + OFFLINE threshold window
        offlineSinceTs = hbTs + (OFFLINE_AT * HB_INTERVAL_MS);
    }
} else {
    offlineSinceTs = 0;
}

var newMsgType = "NO_EVENT";
if (!previouslyOffline && currentlyOffline) {
    newMsgType = "OFFLINE_START";
} else if (previouslyOffline && !currentlyOffline) {
    newMsgType = "OFFLINE_END";
}

// Attributes to save (WHITELIST ONLY)
var out = {
  psuCommPath: psuCommPath,
  offlineSinceTs: offlineSinceTs
  // add latestHeartbeatTs ONLY if you intentionally want it saved here:
  // latestHeartbeatTs: hbTs
};

// Transition flags (for the next step: event logging)
metadata.offlineStartEvent = (!previouslyOffline && currentlyOffline && offlineSinceTs > 0);
metadata.offlineEndEvent = (previouslyOffline && !currentlyOffline);
metadata._nowTs = nowTs;
metadata._prevOfflineSinceTs = prevOfflineSinceTs;
metadata._offlineSinceTs = offlineSinceTs;




return { msg: out, metadata: metadata, msgType: newMsgType };

