const nowTs = Date.now();

// latestHeartbeatTs was loaded by the originator attributes node
const hbRaw = metadata.ss_latestHeartbeatTs;
const hbTs = hbRaw != null ? Number(hbRaw) : 0;

let psuOfflineLive = false;
let psuCommPath = "UNKNOWN";

if (hbTs > 0) {
    const delta = nowTs - hbTs;

    // Get heartbeat interval from attributes, default 30s
    let hbIntervalMs = 30000; // fallback

    if (metadata.ss_heartbeatIntervalMs != null) {
        hbIntervalMs = Number(metadata.ss_heartbeatIntervalMs);
    } else if (msg.heartbeatIntervalMs != null) {
        hbIntervalMs = Number(msg.heartbeatIntervalMs);
    }

    // How many heartbeat cycles have we effectively missed?
    const cycles = delta / hbIntervalMs;

    if (cycles <= 3) {                     // <= 3 cycles
        psuOfflineLive = false;
        psuCommPath = "OK";
    } else if (cycles <= 10) {             // >3 and <=10 cycles
        psuOfflineLive = false;
        psuCommPath = "STALE_HEARTBEAT";
    } else {                               // >10 cycles
        psuOfflineLive = true;
        psuCommPath = "OFFLINE";
    }
}

// attach results to msg
msg.psuOfflineLive = psuOfflineLive;
msg.psuCommPath    = psuCommPath;

// --- OFFLINE SINCE CALC (simple + with reset) -----------------

let offlineSinceTs = 0;

// Only track when truly OFFLINE.
// Start at the timestamp of the last heartbeat we saw.
if (psuCommPath === "OFFLINE" && hbTs > 0) {
    offlineSinceTs = hbTs;
}

// Add to msg so it gets saved
msg.offlineSinceTs = offlineSinceTs;


// --- LAST OUTAGE TRACKING ---------------------------------

const prevOfflineSince = metadata.ss_offlineSinceTs
    ? Number(metadata.ss_offlineSinceTs)
    : 0;

let lastOutageStartTs = metadata.ss_lastOutageStartTs
    ? Number(metadata.ss_lastOutageStartTs)
    : 0;

let lastOutageDurationMs = metadata.ss_lastOutageDurationMs
    ? Number(metadata.ss_lastOutageDurationMs)
    : 0;

const currentlyOffline = (psuCommPath === "OFFLINE");
const previouslyOffline = (prevOfflineSince > 0);

// Detect recovery: was offline before, now online/stale
if (previouslyOffline && !currentlyOffline) {
    lastOutageStartTs = prevOfflineSince;
    lastOutageDurationMs = nowTs - prevOfflineSince;
}

// Save them
msg.lastOutageStartTs = lastOutageStartTs;
msg.lastOutageDurationMs = lastOutageDurationMs;

// --- OUTAGE EVENT FLAG FOR HISTORY ---------------------------

// Default: no outage event
metadata.outageEvent = "false";

// Detect recovery: was offline before, now not offline
if (previouslyOffline && !currentlyOffline) {
    metadata.outageEvent = "true";
}


return {
    msg: msg,
    metadata: metadata,
    msgType: "POST_ATTRIBUTES_REQUEST"
};

